package com.syntax.class02;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Arithmetic Operators +, -, /, *, %
		int num1, num2;
		num1=10;
		num2=14;
		System.out.println(num1+num2);
		System.out.println(num2-num1);
		 
		double num3=10.99;
		double num4=20.99;
		double multi= num3*num4;
		System.out.println(multi);
		
		double div=num4/num3;
		System.out.println("The result of division of 2 doubles = "+div);//1.9099181073703364
		
		
		float f=10.99f;
		float f1=20.99f;
		System.out.println("The result of division of 2 floats = "+f1/f);//1.9099181

		
	}

}
