package com.syntax.class03;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double val=10.99;
double val1=20.99;

if(val>val1) {
	System.out.println("Double value"+val+"is larger than"+val1);
	
} else {
	System.out.println("Double value "+val1+ "is larger than "+ val);
}


	}
}
